mapper.select:
SELECT COUNT(*)
FROM comments
WHERE video_id = #{videoId}

mapper.update:
UPDATE comments
SET like_count = like_count + #{likeCount}
WHERE id = #{commentId}

UPDATE comments
SET dislike_count = dislike_count + #{dislikeCount}
WHERE id = #{commentId}

mapper.delete:
DELETE
FROM comment_likes
WHERE user_id = #{userId}
  AND comment_id = #{commentId}
